import React, { useState } from "react";
import styled from "styled-components";

import Button from "../../UI/Button/Button";
import "./CourseInput.css";

// const FormControlStyle = styled.div`
//   margin: 0.5rem 0;

//   & label {
//     font-weight: bold;
//     display: block;
//     margin-bottom: 0.5rem;
//     color: 
//   }

//   & input {
//     display: block;
//     width: 100%;
//     border: 1px solid #ccc;
//     font: inherit;
//     line-height: 1.5rem;
//     padding: 0 0.25rem;
//   }

//   & input:focus {
//     outline: none;
//     background: #fad0ec;
//     border-color: #8b005d;
//   }
// `;

const CourseInput = (props) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [isValid, setIsValid] = useState(true)

  const goalInputChangeHandler = (event) => {
    console.log(enteredValue)
    setEnteredValue(event.target.value);
  };

  const textFormatter = (text) => {
    const firstSentence = text.split(" ")[0];
    const senteceFormated = firstSentence.replace(
      firstSentence[0],
      firstSentence[0].toUpperCase()
    );
    const mergingFormater =
      senteceFormated + text.slice(senteceFormated.length);
    return mergingFormater;
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();

    console.log(isValid)

    // if (enteredValue.trim().length < 1) setIsValid(false);

    props.onAddGoal(textFormatter(enteredValue));

    setEnteredValue("");
  };

  return (
    <FormControlStyle onSubmit={formSubmitHandler}>
      <div className="form-control">
        <label>Course Goal</label>
        <input
          type="text"
          value={enteredValue}
          onChange={goalInputChangeHandler}
        />
      </div>
      <Button type="submit">Add Goal</Button>
    </FormControlStyle>
  );
};

export default CourseInput;
